import { useState } from 'react';
import close from './assets/close.svg';
import cookie from './assets/cookie.svg';




function esconder (){

};


function AlertCard({type, message, children}) {
  return (
      <div className='card'>
          <img onClick = {esconder} src={close} alt='Close' className='close-button' />
          <img src={cookie} alt='cookie' className='icon' />
          <p>
              {children}
          </p>
          <button onClick = {esconder} className={type}>
              {message}
          </button>
      </div>
  )
};

function App() {  

  return (    
      <div className='App'>
          <div className='card'>
          <img onClick = {esconder} src={close} alt='Close' className='close-button' />
          <img src={cookie} alt='cookie' className='icon' />
          <p>
              {children}
          </p>
          <button onClick = {esconder} className={type}>
              {message}
          </button>
      </div>
  )         
      </div>  
  );
}

export default App;
